mapper.select:
SELECT  STU_CD, LEC_CD
FROM WISH_LECTURE
WHERE STU_CD = #{stuCd}

mapper.update:

	SELECT LEC_HCNT FROM LECTURE
	WHERE LEC_CD = #{lecCd}

UPDATE LECTURE
SET LEC_HCNT = (#{lecHcnt} + 1)
WHERE LEC_CD = #{lecCd}


	SELECT LEC_HCNT FROM LECTURE
	WHERE LEC_CD = #{lecCd}

UPDATE LECTURE
SET LEC_HCNT = (#{lecHcnt} - 1)
WHERE LEC_CD = #{lecCd}

mapper.select:
