mapper.update:

	SELECT LECN_CNT FROM LECTURE_NOTICE
	WHERE LECN_CD = #{lecnCd}

UPDATE LECTURE_NOTICE
SET LECN_CNT = (#{lecnCnt} + 1)
WHERE LECN_CD = #{lecnCd}

mapper.delete:
DELETE 	FROM ENROLMENT
WHERE 	STU_CD = #{stuCd}
AND		LEC_CD = #{lecCd}

DELETE 	FROM LECTURE_GRADE
WHERE 	CRTR_CD = #{crtrCd}
AND		STU_CD = #{stuCd}

