mapper.update:

	SELECT BD_CNT FROM BOARD
	WHERE BD_CD = #{bdCd}

UPDATE BOARD
SET BD_CNT = (#{bdCnt} + 1)
WHERE BD_CD = #{bdCd}

mapper.delete:
UPDATE BOARD
SET    BD_YN = 1, BD_UDT=SYSDATE
WHERE  BD_CD = #{bdCd}

mapper.select:
SELECT T.* FROM ( SELECT DENSE_RANK() OVER( ORDER BY B.BD_REG DESC) RNUM, BD_CD, B.USER_CD, B.COMD_CD, BD_TITLE, BD_CON, BD_REG, BD_UDT, BD_CNT, U.FILE_CD, USER_NM, USER_NME, USER_TEL, USER_ZIP, USER_ADDR1, USER_ADDR2, USER_REG1, USER_REG2, USER_MAIL, USER_PASS, USER_BANK, USER_DEPO, USER_ACC, FILE_SN, FILE_PATH, FILE_ORNM, FILE_SVNM, FILE_EXTSN, FILE_CON, FILE_SIZE, FILE_USED FROM BOARD B INNER JOIN USERS U ON B.USER_CD = U.USER_CD LEFT OUTER JOIN FILES_DETAIL F ON B.BD_CD = F.FILE_CD WHERE USER_CD = #{userCd}
AND B.BD_YN = 0
) T WHERE T.RNUM BETWEEN (${pageNum} * 20) - 19 AND ${pageNum} * 20
