mapper.insert:
INSERT INTO TEST (
    TEST_CD
    , LEC_CD
    , TEST_OPT
    , TEST_NM
    , TEST_CON
    , TEST_REG
    , TEST_DT
    , TEST_LMT
) VALUES (
    #{testCd}
  , #{lecCd}
  , #{testOpt}
  , #{testNm}
  , #{testCon}
  , SYSDATE
  , #{testDt}
  , #{testLmt}
)

INSERT INTO TEST_QUESTION (
    TEQ_CD
    , TEST_CD
    , TEQ_NO
    , TEQ_CON
    , TEQ_OP1
    , TEQ_OP2
    , TEQ_OP3
    , TEQ_OP4
    , TEQ_OP5
    , TEQ_ANSWER
    , TEQ_ALLOT
) VALUES (
    #{testCd} || TRIM(TO_CHAR(#{teqNo}, '00'))
  , #{testCd}
  , #{teqNo}
  , #{teqCon}
  , #{teqOp1}
  , #{teqOp2}
  , #{teqOp3}
  , #{teqOp4}
  , #{teqOp5}
  , #{teqAnswer}
  , #{teqAllot}
)

mapper.select:
SELECT TEST_CD, LEC_CD, TEST_OPT, TEST_NM, TEST_CON, TEST_REG, TEST_DT, TEST_LMT
FROM   TEST
WHERE  TEST_CD = #{testCd}

mapper.update:
	UPDATE TEST
	SET
	    TEST_NM = #{testNm}
	    , TEST_CON = #{testCon}
	    , TEST_DT = #{testDt}
	    , TEST_LMT = #{testLmt}
	WHERE
       TEST_CD = #{testCd}

mapper.insert:

	SELECT 'TEAP' || TRIM(TO_CHAR(TEST_SEQ.NEXTVAL))
	FROM   DUAL

INSERT INTO TEST_APPLY (
    TEA_CD
    , TEST_CD
    , STU_CD
) VALUES (
    #{teaCd}
  , #{testCd}
  , #{stuCd}
)

INSERT INTO TEST_DETAIL (
    TEQ_CD
    , TEA_CD
    , TED_ANSWER
    , TED_YN
) VALUES (
    #{teqCd}
  , #{teaCd}
  , #{tedAnswer}
  , #{tedYn}
)

mapper.update:
	UPDATE TEST_APPLY
	SET
	    TEA_SCORE = #{teaScore}
	WHERE
        TEA_CD = #{teaCd}
